mapper.select:
      SELECT role.id, role.name, role.description, role.available
      FROM role INNER JOIN user_role
	  ON role.id = user_role.role_id AND user_role.user_id = #{userId}

       DROP TEMPORARY TABLE IF EXISTS temp_user_role_permission;
			  DROP TEMPORARY TABLE IF EXISTS new_table;

			 CREATE TEMPORARY TABLE  temp_user_role_permission
			 SELECT role_permission.role_id, role_permission.permission_id
        FROM user_role INNER JOIN role_permission
				ON role_permission.role_id = user_role.role_id AND user_role.user_id = #{userId};

				SELECT permission.*
				FROM permission, temp_user_role_permission
				WHERE permission.id = temp_user_role_permission.permission_id;
